INSERT INTO `blog_infos` VALUES (58, 58, '<pre><span>/**<br></span><span> * 将业务加入事务控制<br></span><span> * </span><span>@param </span><span>service<br></span><span> </span><span>* </span><span>@throws </span><span>Exception<br></span><span> */<br></span><span>public static void </span><span>transaction</span>(TransactionService service) <span>throws </span>Exception{<br>   Connection conn = <span>getConnection</span>()<span>;<br></span><span>   </span>Integer level = <span>TRANSACTION_LEVEL</span>.get()<span>;<br></span><span>   </span>Boolean autoCommit = <span>null;<br></span><span>   if</span>(level==<span>null</span>){<br>      autoCommit = conn.getAutoCommit()<span>;<br></span><span>      </span>level = <span>1</span><span>;<br></span><span>   </span>}<span>else</span>{<br>      level = level + <span>1</span><span>;<br></span><span>   </span>}<br>   <span>TRANSACTION_LEVEL</span>.set(level)<span>;<br></span><span>   try </span>{<br>      <span>if</span>(conn.getAutoCommit()){<br>         conn.setAutoCommit(<span>false</span>)<span>;<br></span><span>      </span>}<br>      service.execute()<span>;<br></span><span>      </span>level = level - <span>1</span><span>;<br></span><span>      </span><span>TRANSACTION_LEVEL</span>.set(level)<span>;<br></span><span>      if</span>(level==<span>0</span>){<br>         conn.commit()<span>;<br></span><span>         </span><span>TRANSACTION_LEVEL</span>.remove()<span>;<br></span><span>      </span>}<br>   }<span>catch </span>(Exception e){<br>      <span>if</span>(conn!=<span>null</span>){<br>         <span>try </span>{<br>            conn.rollback()<span>;<br></span><span>            </span><span>/**<br></span><span>             * 数据回滚的时候，要清除此次操作中涉及到的缓存,<br></span><span>             * 只对使用了CacheUtils的Pojo有效，并且Pojo中的cacheUtils需要设置keepCacheOfThisThread为true<br></span><span>             */<br></span><span>            </span>CacheUtils.<span>clearCacheOfThisThread</span>()<span>;<br></span><span>         </span>} <span>catch </span>(SQLException e1) {<br>            <span>throw new </span>DBException(e)<span>;<br></span><span>         </span>}<br>      }<br>      <span>if</span>(e <span>instanceof </span>SQLException){<br>         <span>throw new </span>DBException(e)<span>;<br></span><span>      </span>}<span>else</span>{<br>         <span>throw </span>e<span>;<br></span><span>      </span>}<br>   } <span>finally </span>{<br>      <span>if</span>(autoCommit!=<span>null</span>){<br>         conn.setAutoCommit(autoCommit)<span>;<br></span><span>      </span>}<br>   }<br>}\r\n\r\n<pre>QueryHelper.<span>transaction</span>(<span>new </span>TransactionService() {<br>   <span>@Override<br></span><span>   </span><span>public void </span><span>execute</span>() <span>throws </span>Exception {<br>      <span>// 买家流水<br></span><span>      </span><span>long </span>id1 = PayWater.<span>ME</span>.saveIt(<span>order</span>.getBuyer_id()<span>, </span>PayWater.<span>TYPE_IN_RETURN</span><span>, </span><span>l_money</span><span>, </span><span>order</span>.getSeller_id()<span>, </span>PayOrder.<span>STATUS_CREATE</span><span>, </span><span>0</span><span>, </span><span>order</span>.getId()<span>,<br></span><span>            </span><span>order</span>.getOrder_type()<span>, </span>PayOrder.<span>PAY_TYPE_BALANCE</span><span>, </span><span>order</span>.getBuyer_id())<span>;<br></span><span>      </span>PayWater.<span>ME</span>.modRemark(id1<span>, </span><span>remark</span><span>, </span><span>0</span><span>, </span><span>user</span>.getId())<span>;<br></span><span>      </span><span>// 卖家流水<br></span><span>      </span><span>long </span>id2 = PayWater.<span>ME</span>.saveIt(<span>order</span>.getSeller_id()<span>, </span>PayWater.<span>TYPE_OUT_RETURN</span><span>, </span><span>l_money</span><span>, </span><span>order</span>.getBuyer_id()<span>, </span>PayOrder.<span>STATUS_CREATE</span><span>, </span>id1<span>, </span><span>order</span>.getId()<span>,<br></span><span>            </span><span>order</span>.getOrder_type()<span>, </span>PayOrder.<span>PAY_TYPE_BALANCE</span><span>, </span><span>order</span>.getBuyer_id())<span>;<br></span><span>      </span>PayWater.<span>ME</span>.modRemark(id2<span>, </span><span>remark</span><span>, </span><span>0</span><span>, </span><span>user</span>.getId())<span>;<br></span><span>   </span>}<br>})<span>;</span></pre><p>\r\n</p></pre>', 0, 0, NULL, 1, 1, '2019-01-25 13:04:08', '2019-01-25 13:05:47');
INSERT INTO `blog_infos` VALUES (59, 59, '<pre lay-lang=\"Java\">package com.lifes77.web;\r\n\r\nimport java.util.Scanner;\r\n\r\npublic class Test {\r\n\r\n    public static void main(String[] args) {\r\n\r\n        Scanner input = new Scanner(System.in);\r\n        int NUM = 5;\r\n        int[] a = new int[NUM];\r\n        System.out.println(\"请输入数字:\");\r\n\r\n        for (int i = 0; i &lt; a.length; i++) {\r\n            a[i] = input.nextInt();\r\n        }\r\n\r\n        System.out.println(\"排序之前:\");\r\n        for (int i : a) {\r\n            System.out.print(i + \"\\t\");\r\n        }\r\n\r\n        //冒泡排序实现\r\n        for (int i = 0; i &lt; a.length - 1; i++) {\r\n            for (int j = 0; j &lt; a.length - i - 1; j++) {\r\n                if (a[j] &gt; a[j + 1]) {\r\n                    int temp = a[j];\r\n                    a[j] = a[j + 1];\r\n                    a[j + 1] = temp;\r\n                }\r\n            }\r\n        }\r\n\r\n        System.out.println(\"\\n排序之后:\");\r\n        for (int i : a) {\r\n            System.out.print(i + \"\\t\");\r\n        }\r\n        System.out.print(\"\\n\");\r\n    }\r\n\r\n}\r\n</pre>', 0, 0, NULL, 1, 1, '2019-02-12 16:40:30', '2019-02-12 16:40:51');
