INSERT INTO `questions` VALUES (5, 2, 'ajax所有请求完毕后后再执行函数操作', '<pre>$.when(\r\n    /**\r\n     * 今日注册用户数\r\n     */\r\n    $.get(\"/user/register/count/today\",\r\n        function (data) {\r\n            $(\".todayRegister span\").text(data.count);\r\n        }\r\n    ),\r\n\r\n    /**\r\n     * 今日登录用户数\r\n     */\r\n    $.get(\"/user/login/count/today\",\r\n        function (data) {\r\n            $(\".todayLogin span\").text(data.count);\r\n        }\r\n    ),\r\n\r\n    /**\r\n     * 系统用户数\r\n     */\r\n    $.get(\"/user/stat\",\r\n        function (data) {\r\n            $(\".userAll span\").text(data.count);\r\n        }\r\n    ),\r\n\r\n    /**\r\n     * 图片总数\r\n     */\r\n    $.get(\"/up/stat\",\r\n        function (data) {\r\n            $(\".imgAll span\").text(data.count);\r\n        }\r\n    )\r\n).done(function () {\r\n    /**\r\n     * 数字格式化\r\n     */\r\n    $(\".panel span\").each(function () {\r\n        $(this).html($(this).text() &gt; 9999 ? ($(this).text() / 10000).toFixed(2) + \"&lt;em&gt;万&lt;/em&gt;\" : $(this).text());\r\n    })\r\n\r\n}).fail(function () {\r\n    layer.msg(\"系统正在开差中呢~~~~\", {icon: 5});\r\n});</pre>\n<p>$.when里面的异步请求都完了之后，再执行done里面的函数</p>', 0, 0, NULL, 301, 0, 0, 1, '2019-11-25 16:09:55', '2020-07-27 23:15:15', 0, 0, 0, NULL, 7, '2019-11-25 16:39:03', 0, 0, 1, 0, NULL);
